sys_variable_value.value:
(function execute(inputs, outputs) {

  var attachmentSysId = inputs.attachmentSysId;
  var highlightedKeys = inputs.highlightedKeys;

  var columns = [];
  var data = [];
  var dataStrings = [];
  var highlightedStrings = columns.concat(highlightedKeys);//make highlighted strings = to the keys

  var attachment = new GlideRecord('sys_attachment');
  if (attachment.get(attachmentSysId)) {
    var sa = new GlideSysAttachment();
    var attachmentStream = sa.getContentStream(attachmentSysId);
	gs.info('data attachment stream : ' + attachmentStream);
    if (attachmentStream != null) {
      var reader = new GlideTextReader(attachmentStream);
      var ln;
      var csvLines = [];

      while((ln = reader.readLine()) != null) {
        csvLines.push(ln);
      }
      gs.info('data csvlines : ' + csvLines);

      if (columns.length === 0 && csvLines.length > 0) {
        columns = csvLines.shift().split(',');

        if (columns[0].charCodeAt(0) === 0xFEFF) {
          columns[0] = columns[0].slice(1);
        }
      }

      csvLines.forEach(function(line) {
        var values = line.split(',');

        var rowData = {};
        var highlightedRowString = '';
        var regularRowString = '';

        columns.forEach(function(column, index) {
          if (values[index] && values[index].trim() !== '') {
            rowData[column] = values[index];

            // Check if the key is in the highlighted keys
            if (highlightedKeys.indexOf(column) !== -1) {
              highlightedRowString += column + ': ' + values[index] + '\n';
              //push to the highlighted array
              //highlightedStrings.push(values[index]); //push only the value
              highlightedStrings[highlightedKeys.indexOf(column)] = values[index]; //this should make it be ordered in the same way as the input
            } else {
              regularRowString += column + ': ' + values[index] + '\n';
            }
          }
        });

        if (Object.keys(rowData).length > 0) {
          data.push(rowData);
        }

        var rowString = highlightedRowString + '\n\n' + regularRowString;

        if (rowString !== '') {
          dataStrings.push(rowString.trim());
        }
      });
    }
  }

  outputs.data = data;
  outputs.datastrings = dataStrings;
  outputs.highlightedstrings = highlightedStrings;
  gs.info('data : ' + data);
  gs.info('data string : ' + dataStrings);

})(inputs, outputs);

